def PatternCount(Text, Pattern):
    count = 0
    for i in range((len(Text)+1)-len(Pattern)):
        print("Iteration "+str(i)+": Text: "+Text[i:i+len(Pattern)] + "=")
        if Text[i:i+len(Pattern)] == Pattern:
            count += 1

    print("Count: ")
    print(count)
    return count


Text = '' 
Pattern = 'TGTGCCATG'
print(PatternCount(Text, Pattern))